  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'component_vhdpcore',
	title: 'VHDP Core'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Core Overview',
  id: 'core-overview',
  children: [{
    value: 'Specs',
    id: 'specs',
    children: []
  }, {
    value: 'As Arduino',
    id: 'as-arduino',
    children: []
  }]
}];
export const metadata = {
  "id": "component_vhdpcore",
  "title": "VHDP Core",
  "description": "![VHDP Core M](assets/vhdpcore/Items.png)\r",
  "source": "@site/..\\docs\\component_vhdpcore.md",
  "permalink": "/docs/component_vhdpcore",
