  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Get Started with VHDP',
	sidebar_label: 'Install',
	description: 'Start using VHDP quickly. The FPGA Revolution is waiting for you',
	keywords: [
		'vhdp',
		'vhdplus',
		'getstarted',
		'fpga'
	],
	image: '/img/logo.PNG'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Get Started',
  id: 'get-started',
  children: [{
    value: 'Install VHDP IDE',
    id: 'install-vhdp-ide',
    children: []
  }, {
    value: 'Drivers',
    id: 'drivers',
    children: []
  }, {
    value: 'Connect VHDPlus IDE with Quartus',
    id: 'connect-vhdplus-ide-with-quartus',
    children: []
  }, {
    value: 'Addional Programs',
    id: 'addional-programs',
    children: []
  }]
}, {
  value: 'Program with VHDP',
  id: 'program-with-vhdp',
  children: [{
    value: 'Create your first program:',
    id: 'create-your-first-program',
    children: []
  }]
}];
export const metadata = {
  "id": "getstarted",
  "title": "Get Started with VHDP",
  "description": "Start using VHDP quickly. The FPGA Revolution is waiting for you",
  "source": "@site/..\\docs\\getstarted.md",
  "permalink": "/docs/getstarted",
  "lastUpdatedBy": "Author",
  "lastUpdatedAt": 1539502055,
  "sidebar_label": "Install",
  "sidebar": "docs",
  "next": {
    "title": "Install Drivers",
    "permalink": "/docs/getstarted_drivers"
  }
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc,
metadata
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "get-started"
    }}>{`Get Started`}</h2>
    <p>{`Use the benefits of code suggestions and vendor-independent libraries by using VHDP IDE, the easiest way to program your FPGA.`}</p>
    <p><img parentName="p" {...{
        "src": "/img/getstarted/IDE.PNG",
        "alt": "VHDP IDE"
      }}></img></p>
    <h3 {...{
      "id": "install-vhdp-ide"
    }}>{`Install VHDP IDE`}</h3>
    <ol>
      <li parentName="ol">{`Download `}<a href="http://download.altera.com/akdlm/software/acdsinst/18.1std/625/ib_installers/max10-18.1.0.625.qdz" target="_blank">{`MAX 10 device support`}</a>{`¹`}</li>
      <li parentName="ol">{`Download and install `}<a href="http://download.altera.com/akdlm/software/acdsinst/18.1std/625/ib_installers/QuartusLiteSetup-18.1.0.625-windows.exe" target="_blank">{`Quartus Prime Lite`}</a>{` or `}<a href="http://download.altera.com/akdlm/software/acdsinst/18.1std/625/ib_installers/QuartusLiteSetup-18.1.0.625-linux.run" target="_blank">{`Quartus Prime Lite for Linux`}</a>{`²`}</li>
      <li parentName="ol">{`Choose your version of VHDP IDE according to your operating system:`}</li>
    </ol>
    <p><strong parentName="p">{`Important Notes:`}</strong></p>
    <p><strong parentName="p">{`At this point VHDP has not left WIP state. Breaking changes might occur until version 1.0.`}</strong></p>
    <p><strong parentName="p">{`At this point VHDP IDE does not obtain a Microsoft Certificate. According to that Windows installer might get blocked by SmartScreen. If you have trouble you can try to use the non installer version:`}</strong></p>
    <ul>
      <li parentName="ul">{`32 Bit: {@inject: Win32ZipDownload}`}</li>
      <li parentName="ul">{`64 Bit: {@inject: Win64ZipDownload}`}</li>
    </ul>
    <p>{`Just extract the downloaded `}<inlineCode parentName="p">{`.zip`}</inlineCode>{` to a location of your choice and execute `}<inlineCode parentName="p">{`VHDPlus.exe`}</inlineCode>{`.`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Operating System`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`32 Bit`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`64 Bit`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Windows 7+`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{@inject: Win32Download}`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{@inject: Win64Download}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Debian based (Ubuntu, Linux Mint, ...)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Not supported`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{@inject: DEB64Download}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`RPM based (CentOS, Fedora, OpenSUSE, ...)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Not supported`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{@inject: RPM64Download}`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Other linux`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Not supported`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`{@inject: TAR64Download}`}</td>
        </tr>
      </tbody>
    </table>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Install the Downloaded VHDP Package:`}</li>
    </ol>
    <ul>
      <li parentName="ul"><strong parentName="li">{`Windows`}</strong>{`: Execute the downloaded `}<inlineCode parentName="li">{`.msi`}</inlineCode>{` file and follow the steps on screen.`}</li>
      <li parentName="ul"><strong parentName="li">{`Debian`}</strong>{`: Open the downloaded `}<inlineCode parentName="li">{`.deb`}</inlineCode>{` file with your built-in package manager `}<strong parentName="li">{`OR`}</strong>{` install it install it by using the terminal `}<inlineCode parentName="li">{`$ sudo dpkg –i <PATH TO DOWNLOADED PACKAGE>`}</inlineCode></li>
      <li parentName="ul"><strong parentName="li">{`RPM`}</strong>{`: Open the downloaded `}<inlineCode parentName="li">{`.rpm`}</inlineCode>{` file with your built-in package manager `}<strong parentName="li">{`OR`}</strong>{` install it install it by using the terminal `}<inlineCode parentName="li">{`$ sudo rpm –i <PATH TO DOWNLOADED PACKAGE>`}</inlineCode></li>
      <li parentName="ul"><strong parentName="li">{`Other Linux`}</strong>{`: Unpack the contents of the `}<inlineCode parentName="li">{`tar.gz`}</inlineCode>{` to any location and execute the `}<inlineCode parentName="li">{`VHDPlus`}</inlineCode>{` executable to start the app.`}</li>
    </ul>
    <p>{`¹MAX 10 device support
he VHDP Core is equipped with a MAX 10 FPGA. Select the device family depending on your development board.`}</p>
    <p>{`²Quartus Prime
Quartus Prime is necessary for compiling your code and programming your FPGA.`}</p>
    <h3 {...{
      "id": "drivers"
    }}>{`Drivers`}</h3>
    <p>{`You need to install the drivers according to your programmer at hand in order to program your FPGA. Our VHDP Core features an ARROW USB Programmer because of its additional USB to UART interface.
Follow `}<strong parentName="p"><a parentName="strong" {...{
          "href": "/docs/getstarted_drivers"
        }}>{`this guide`}</a></strong>{` to download and install the driver for the `}<strong parentName="p">{`Arrow USB Programmer`}</strong>{`.`}</p>
    <h3 {...{
      "id": "connect-vhdplus-ide-with-quartus"
    }}>{`Connect VHDPlus IDE with Quartus`}</h3>
    <p>{`For direct compiling and programming of VHDP IDE a connection via Quartus is necessary.
If Quartus is installed in default directory no further steps are needed.
Otherwise an adjustment of the Quartus' path can be realized by opening the VHDPlus IDE and navigating through`}<inlineCode parentName="p">{`Extras -> Options -> General`}</inlineCode></p>
    <p><img parentName="p" {...{
        "src": "/img/getstarted/QuartusPath.png",
        "alt": "Select Quartus Path"
      }}></img></p>
    <p>{`When Quartus is detected successfully the boundary around Quartus' path eventuelly turns green.`}</p>
    <h3 {...{
      "id": "addional-programs"
    }}>{`Addional Programs`}</h3>
    <h4 {...{
      "id": "code-simulation"
    }}>{`Code Simulation:`}</h4>
    <p>{`With GHDL (recommended):
1. Install GHDL with `}<a href="http://ghdl.free.fr/ghdl-installer-0.29.1.exe" target="_blank">{`the Installer for Windows`}</a>{` or with `}<inlineCode parentName="p">{`sudo apt-get install ghdl`}</inlineCode>{` on linux
2. Download an extract `}<a href="http://gtkwave.sourceforge.net/gtkwave-3.3.103.tar.gz" target="_blank">{`GTK Wave for Windows`}</a>{` or install it with `}<inlineCode parentName="p">{`sudo apt-get install gtkwave`}</inlineCode>{` on linux
3. Specify the path to the "gtkwave" folder in Extras/Settings/Simulator/GTKWave path
4. Read our instructions `}<a parentName="p" {...{
        "href": "/docs/getstarted_ghdl"
      }}>{`here`}</a></p>
    <p>{`With ModelSim:
1. Download and install `}<a href="http://download.altera.com/akdlm/software/acdsinst/18.1std/625/ib_installers/ModelSimSetup-18.1.0.625-windows.exe" target="_blank">{`ModelSim`}</a>{` or `}<a href="http://download.altera.com/akdlm/software/acdsinst/18.1std/625/ib_installers/ModelSimSetup-18.1.0.625-linux.run" target="_blank">{`ModelSim for Linux`}</a>{`
2. Read our instructions `}<a parentName="p" {...{
        "href": "/docs/getstarted_modelsim"
      }}>{`here`}</a></p>
    <h2 {...{
      "id": "program-with-vhdp"
    }}>{`Program with VHDP`}</h2>
    <p>{`Creating a program with the VHDP IDE is simple, but to get started even faster we have created tutorials including a written instruction:`}</p>
    <p>{`First Program:`}</p>
    <div class="fluidMedia"><iframe id="ytplayer" type="text/html" width="100%" src="https://www.youtube.com/embed/oGBgobUQ0bU?autoplay=0&origin=http://vhdplus.com" frameborder="0" allowfullscreen></iframe></div>
    <h3 {...{
      "id": "create-your-first-program"
    }}>{`Create your first program:`}</h3>
    <p>{`Manual:
1. Start VHDP IDE and create a new project (top left) which automatically creates a .vhdp file.
2. Inside the vhdp file you can write your code. More information about this `}<a parentName="p" {...{
        "href": "/docs/getstarted_vhdp"
      }}>{`here`}</a>{`. To get a better understanding as a beginner use this demo code:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-vhdp"
      }}>{`/*
Blink example
*/

Main
(
    --I/Os of the FPGA
    LED : OUT STD_LOGIC := '0';  --STD_LOGIC contains states like '1', '0' or 'Z' for High Impedance
)     
{   
    Process Demo ()
    {
        Thread --Every Thread is an endless loop
        {
            LED <= '1';
            Wait(250ms);
            LED <= '0';
            Wait(250ms);
        }
    }
}
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`If no errors appear while analyzing you can click on create (green arrow) and a window opens up. In it you can create connections of in- and outputs from Main to physical I/Os (they are labelled on the development board and shield). Before clicking on OK you should check if Quartus' path is correct. On Windows this path is usually "C:\\intelFPGA_lite\\18.1\\quartus".`}</li>
      <li parentName="ol">{`In case of errors while compiling you can double click on the error in the error list. When you have found the error, you have to fix the error within the .vdhp file.`}</li>
      <li parentName="ol">{`When compiling is finished, which can last up to several minutes, because the compiler attempts to find the most efficient way to connect the logic elements, you can program the FPGA. In order to do that you have make shure that the correct drivers are installed and to click on Program (blue arrow). If you have problems, use the Quartus programming window next to the Program button.`}</li>
    </ol>

    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
