export const rightToc = [{
  value: 'Shield Overview',
  id: 'shield-overview',
  children: []
}];
export const metadata = {
  "id": "component_vhdpshield",
  "title": "VHDP Shield",
  "description": "![VHDP Shield M](assets/vhdpshield/Items.png)\r",
