  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'ide_editor_autocorrection',
	title: 'Autocorrection',
	sidebar_label: 'Autocorrection'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Overview',
  id: 'overview',
  children: []
}, {
  value: 'Compiler autocorrection',
  id: 'compiler-autocorrection',
  children: []
}, {
  value: 'Signal declaration',
  id: 'signal-declaration',
  children: [{
    value: 'Signals',
    id: 'signals',
    children: []
  }, {
    value: 'Variables',
    id: 'variables',
    children: []
  }, {
    value: 'I/Os',
    id: 'ios',
    children: []
  }, {
    value: 'Arrays',
    id: 'arrays',
    children: []
  }]
}];
export const metadata = {
  "id": "ide_editor_autocorrection",
  "title": "Autocorrection",
  "description": "## Overview\r",
  "source": "@site/..\\docs\\ide_editor_autocorrection.md",
  "permalink": "/docs/ide_editor_autocorrection",
  "lastUpdatedBy": "Author",
