const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc,
metadata
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "overview"
    }}>{`Overview`}</h2>
    <p>{`The syntax of VHDL for the signals and the operators can be confusing for people that worked with other programming languages.
So we included an autocorrection to make the shift easier. Here a list of things that will be replaced if used:`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`String to replace`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Correct string`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Example`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`= or :=/<= if used wrong`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`:= for variables and <= for signals`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Example_var `}<strong parentName="td">{`=`}</strong>{` ... -> Example_var :=`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`!=`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`/= (unequal)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`If (a !`}<strong parentName="td">{`=`}</strong>{` b) -> If (a /= b)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`==`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`= (equal)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`If (a =`}<strong parentName="td">{`=`}</strong>{` b) -> If (a = b)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`&&`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`AND`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`If (a &`}<strong parentName="td">{`&`}</strong>{` b) -> If (a AND b)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`|`}{`|`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`OR`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`If (a `}{`|`}<strong parentName="td">{`|`}</strong>{` b) -> If (a OR b)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`i ++`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`i := i + 1`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`i +=`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`i := i +`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`i -=`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`i := i -`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "compiler-autocorrection"
    }}>{`Compiler autocorrection`}</h2>
    <p>{`If the frequency of the CLK signal is 12MHz, you can write Wait(100ms); and the time is converted to CLK cycles. Possile time units are s, ms, us and ns.`}</p>
    <p>{`If you are using `}<a parentName="p" {...{
        "href": "https://github.com/leonbeier/VHDPlus_Libraries_and_Examples/tree/master/Examples/Basics/String"
      }}>{`strings`}</a>{`, you can write s"..." to convert this to a HEX value while compiling. `}</p>
    <h2 {...{
      "id": "signal-declaration"
    }}>{`Signal declaration`}</h2>
    <p>{`The text will be replaced with pressing the last char of the string to replace. If you paste a text, you e.g. have to delete and write the ';' again to replace the string.`}</p>
    <h3 {...{
      "id": "signals"
    }}>{`Signals`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-vhdp"
      }}>{`INTEGER i = 0;
-->
SIGNAL i : INTEGER := 0;
`}</code></pre>
    <h3 {...{
      "id": "variables"
    }}>{`Variables`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-vhdp"
      }}>{`NATURAL range 0 to 255 count = 0; 
-->
VARIABLE count : NATURAL range 0 to 255 := 0;
`}</code></pre>
    <h3 {...{
      "id": "ios"
    }}>{`I/Os`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-vhdp"
      }}>{`OUT STD_LOGIC_VECTOR (7 downto 0) LED = (others => '0');
-->
LED : OUT STD_LOGIC_VECTOR(7 downto 0) := (others => '0');
`}</code></pre>
    <h3 {...{
      "id": "arrays"
    }}>{`Arrays`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-vhdp"
      }}>{`INTEGER range 0 to 255 [5] [7] arr = (others => (others => 0));
-->
TYPE arr_type IS ARRAY (0 to 4, 0 to 6) OF INTEGER range 0 to 255;
SIGNAL arr : arr_type := (others => (others => 0));
`}</code></pre>

    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
